import React, { useState } from 'react';

const TaskItem = ({ index, task, editTask, deleteTask }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [updatedTask, setUpdatedTask] = useState(task);
  const [isCompleted, setIsCompleted] = useState(false); // New state for tracking completion

  const handleEdit = () => {
    if (updatedTask.trim() !== '') {
      editTask(index, updatedTask);
      setIsEditing(false);
    }
  };

  const toggleComplete = () => {
    setIsCompleted(!isCompleted); // Toggle the completion state
  };

  return (
    <li style={{ textDecoration: isCompleted ? 'line-through' : 'none' }}>
      {isEditing ? (
        <>
          <input
            type="text"
            value={updatedTask}
            onChange={(e) => setUpdatedTask(e.target.value)}
          />
          <button onClick={handleEdit}>Save</button>
        </>
      ) : (
        <>
          <span>{task}</span>
          <button onClick={() => setIsEditing(true)}>Edit</button>
          <button onClick={() => deleteTask(index)}>Delete</button>
          <button onClick={toggleComplete}>
            {isCompleted ? 'Undo' : 'Done'}
          </button>
        </>
      )}
    </li>
  );
};

export default TaskItem;
